- (IBAction)buttonDigitPressed:(UIButton *)button
{
    NSString *integerPart = [SDKRegistry returnNSStringFromComponent:@"integerPart"];
    NSString *decimalPart = [SDKRegistry returnNSStringFromComponent:@"decimalPart"];
    
    if (!integerPart) integerPart = @"";
    if (!decimalPart) decimalPart = @"";
    
    float value = 0;
    
    if ([decimalPart length] > 9 || [[integerPart stringByAppendingString:decimalPart] length] > 14) return;
    
    if (![self stringComponent:@"." existsInString:integerPart] && ![button.titleLabel.text isEqualToString:@"."])
        value = [[integerPart stringByAppendingString:button.titleLabel.text] doubleValue];
    
    if (![self stringComponent:@"." existsInString:integerPart] && value >= kCalculationLimit)
        integerPart = [SDKRegistry updateRegistryComponent:@"integerPart" withString:
                       [integerPart stringByAppendingString:@"."]];
    
    if (![self stringComponent:@"." existsInString:integerPart] && value < kCalculationLimit){
        integerPart = [SDKRegistry updateRegistryComponent:@"integerPart" withString:
                       [integerPart stringByAppendingString:button.titleLabel.text]];
    }else if (![button.titleLabel.text isEqualToString:@"."] && [self stringComponent:@"." existsInString:integerPart]){
        decimalPart = [SDKRegistry updateRegistryComponent:@"decimalPart" withString:
                       [decimalPart stringByAppendingString:button.titleLabel.text]];
    }
