- (void)backspaceDigits
{
    NSString *integerPart = [SDKRegistry returnNSStringFromComponent:@"integerPart"];
    NSString *decimalPart = [SDKRegistry returnNSStringFromComponent:@"decimalPart"];
    
    int precision = [[integerPart stringByAppendingString:decimalPart] length];
    
    if (!integerPart) decimalPart = @"";
    if (!decimalPart) decimalPart = @"";
    
    if ([decimalPart length] > precision){
        decimalPart = [decimalPart substringWithRange:NSMakeRange(0, precision)];
        decimalPart = [SDKRegistry updateRegistryComponent:@"decimalPart" withString:decimalPart];
    }
    
    if (![decimalPart isEqualToString:@""]){
        decimalPart = [decimalPart substringWithRange:NSMakeRange(0, [decimalPart length] - 1)];
    }else if (![integerPart isEqualToString:@""]){
        if ([[integerPart substringWithRange:NSMakeRange([integerPart length] - 1, 1)] isEqualToString:@"."])
            integerPart = [integerPart substringWithRange:NSMakeRange(0, [integerPart length] - 1)];
        
        integerPart = [integerPart substringWithRange:NSMakeRange(0, [integerPart length] - 1)];
    }
    
    [SDKRegistry updateRegistryComponent:@"integerPart" withString:integerPart];
    [SDKRegistry updateRegistryComponent:@"decimalPart" withString:decimalPart];
}

- (BOOL)stringComponent:(NSString *)component existsInString:(NSString *)targetString
{
    if ([targetString length] == 0) return false;
    
    for (int index = 0; index < [targetString length]; index++){
        if ([[targetString substringWithRange:NSMakeRange(index, 1)] isEqualToString:component]){
            return true;
        }
    }
    
    return false;
}

