- (void)performConversion
{
    NSString *integerPart = [SDKRegistry returnNSStringFromComponent:@"integerPart"];
    NSString *decimalPart = [SDKRegistry returnNSStringFromComponent:@"decimalPart"];
    
    if (!integerPart) integerPart = @"";
    if (!decimalPart) decimalPart = @"";
    
    if ([[integerPart stringByAppendingString:decimalPart] isEqualToString:kStringNothing]){
        [self refreshConverter]; return;
    }
    
    double source = [[integerPart stringByAppendingString:decimalPart] doubleValue];
    double fromRate = [SDKRegistry returnDoubleFromComponent:@"FromCurrencyRate"];
    double toRate = [SDKRegistry returnDoubleFromComponent:@"ToCurrencyRate"];
    
    if (fromRate != 0){
        [labelCurrency2 setText:[[self formatStringNum:integerPart] stringByAppendingString:decimalPart]];
        [labelCurrency1 setText:[self precisionFormatStringNum:[NSString stringWithFormat:@"%f", toRate / fromRate * source]]];
    }else{
        [labelCurrency2 setText:[[self formatStringNum:integerPart] stringByAppendingString:decimalPart]];
        [labelCurrency1 setText:[self precisionFormatStringNum:@"0"]];
    }
    
    //NSLog(@"To Rate: %f, From Rate: %f, Source: %f", toRate, fromRate, source);
}

