- (void)updateRatesDatabase
{
    NSString *savedTab1Display = [[NSUserDefaults standardUserDefaults] stringForKey:@"stringTab1DisplaySave"];
        NSString *savedTab2Display = [[NSUserDefaults standardUserDefaults] stringForKey:@"stringTab2DisplaySave"];
        NSString *savedTab3Display = [[NSUserDefaults standardUserDefaults] stringForKey:@"stringTab3DisplaySave"];
        NSString *savedTab4Display = [[NSUserDefaults standardUserDefaults] stringForKey:@"stringTab4DisplaySave"];
        NSString *savedTab5Display = [[NSUserDefaults standardUserDefaults] stringForKey:@"stringTab5DisplaySave"];
        NSString *savedTab6Display = [[NSUserDefaults standardUserDefaults] stringForKey:@"stringTab6DisplaySave"];
    if ([savedTab1Display isEqualToString:@"Currency Exchange"]||[savedTab2Display isEqualToString:@"Currency Exchange"]||[savedTab3Display isEqualToString:@"Currency Exchange"]||[savedTab4Display isEqualToString:@"Currency Exchange"]||[savedTab5Display isEqualToString:@"Currency Exchange"]||[savedTab6Display isEqualToString:@"Currency Exchange"]) {
        
        /*Downloads Exchange Rates From Google Finance*/
        
        [self setSuccess:NO];
        
        NSString *unescaped = @"http://download.finance.yahoo.com/d/quotes.csv?s=";
        
        unescaped = [unescaped stringByAppendingString:[self exchangeComponentsWithBase:@"USD"]];
        unescaped = [unescaped stringByAppendingString:@"&f=sl1d1t1ban&e=.csv"];
        
        NSURL *url = [NSURL URLWithString:[unescaped stringByAddingPercentEscapesUsingEncoding:NSASCIIStringEncoding]];
        NSURLRequest *request = [NSURLRequest requestWithURL:url];
        AFHTTPRequestOperation *operation = [[AFHTTPRequestOperation alloc] initWithRequest:request];
        
        [operation setCompletionBlockWithSuccess:^(AFHTTPRequestOperation *operation, id responseObject) {
            [self populateTableWithObject:[self stringWithData:responseObject] usingBase:@"USD"];
            [self setSuccess:YES];
            
            [customUITableView reloadData];
            [self performConversion];
            
            [hud2 hide:YES];
            [[[[UIAlertView alloc] initWithTitle:@"Update Exchange Rates"
                                         message:@"Exchange rates were updated successfully."
                                        delegate:nil
                               cancelButtonTitle:@"OK"
                               otherButtonTitles:nil] autorelease] show];
            
            [SDKRegistry updateRegistryComponent:@"Day" withInteger:[[self.dateComponents objectAtIndex:1] integerValue]];
            [SDKRegistry updateRegistryComponent:@"Month" withInteger:[[self.dateComponents objectAtIndex:0] integerValue]];
            [SDKRegistry updateRegistryComponent:@"Year" withInteger:[[self.dateComponents objectAtIndex:2] integerValue]];
            
            [SDKRegistry updateRegistryComponent:@"TimeComponent" withString:[self.timeComponent uppercaseString]];
            
            [labelUpdate setText:[NSString stringWithFormat:@"Rates as at %i %@ %i %@",
                                  [SDKRegistry returnIntFromComponent:@"Day"],
                                  [self resolveMonthDescription:[SDKRegistry returnIntFromComponent:@"Month"]],
                                  [SDKRegistry returnIntFromComponent:@"Year"], [self.timeComponent uppercaseString]]];
        }failure:^(AFHTTPRequestOperation *operation, NSError *error) {
            NSLog(@"Failure code: %i", [operation.response statusCode]);
            NSLog(@"Failed with response string: %@", [operation.responseString stringByTrimmingCharactersInSet:[NSCharacterSet whitespaceAndNewlineCharacterSet]]);
            
            [hud2 hide:YES];
            [[[[UIAlertView alloc] initWithTitle:@"Update Exchange Rates"
                                         message:@"An error occurred while updating exchange rates.  Please retry the operation."
                                        delegate:nil
                               cancelButtonTitle:@"OK"
                               otherButtonTitles:nil] autorelease] show];
            
        }];
        
        [operation start];
        [operation release];
    }
}
- (NSString *)stringWithData:(id)data
{
    return [[[NSString alloc] initWithBytes:[data bytes] length:[data length] encoding:NSUTF8StringEncoding] autorelease];
}

- (NSString *)exchangeComponentsWithBase:(NSString *)base
{
    NSString *result = [[[NSString alloc] initWithString:@""] autorelease];
    
    for (int index = 0; index < [self.content count]; index++){
        NSString *ccode = [self.content objectAtIndex:index];
        
        result = [result stringByAppendingString:
                  [base stringByAppendingString:
                   [ccode stringByAppendingString:@"=X"]]];
        
        if (index < [self.content count]) {result = [result stringByAppendingString:@","];}
    }
    
    return result;
}

- (void)populateTableWithObject:(NSString *)object usingBase:(NSString *)base
{
    NSArray *rows = [object componentsSeparatedByCharactersInSet:[NSCharacterSet newlineCharacterSet]];
    SDKDatabase *database = [[[SDKDatabase alloc] init] autorelease];
    
    for (NSString *row in rows) {
        if ([row length] == 0) { continue; }
        
        NSArray *fields = [row componentsSeparatedByString:@","];
        
        NSString *ccode = [[fields objectAtIndex:0] substringWithRange:NSMakeRange(4, 3)];
        
        NSString *dateComponent = [[fields objectAtIndex:2] stringByReplacingOccurrencesOfString:@"\"" withString:kStringNothing];
        
        double exchangeRate = [ccode isEqualToString:base] ? 1.0f:[[fields objectAtIndex:1] doubleValue];
        
        if (exchangeRate != 0){
            self.dateComponents = [dateComponent componentsSeparatedByString:@"/"];
            self.timeComponent = [[fields objectAtIndex:3] stringByReplacingOccurrencesOfString:@"\"" withString:kStringNothing];
        }
        
        [database updateRateCurrencyWithCode:ccode withRate:exchangeRate];
        
        if ([[SDKRegistry returnNSStringFromComponent:@"FromCurrencyCode"] isEqualToString:ccode]){
            [SDKRegistry updateRegistryComponent:@"FromCurrencyRate" withDouble:exchangeRate];
        }else if ([[SDKRegistry returnNSStringFromComponent:@"ToCurrencyCode"] isEqualToString:ccode]){
            [SDKRegistry updateRegistryComponent:@"ToCurrencyRate" withDouble:exchangeRate];
        }
        
        NSLog(@"%@ = %f %@ %@", ccode, exchangeRate, dateComponent, [timeComponent uppercaseString]);
    }
}

