- (NSString *)formatStringNum:(NSString *)stringNum
{
    NSNumberFormatter *formatter = [[[NSNumberFormatter alloc] init] autorelease];
    
    [formatter setMinimumIntegerDigits:1];
    [formatter setMaximumFractionDigits:10];
    [formatter setNumberStyle:[SDKRegistry returnBoolFromComponent:@"GroupDigits"] ? NSNumberFormatterDecimalStyle:NSNumberFormatterNoStyle];
    
    NSString *result = [formatter stringFromNumber:[formatter numberFromString:stringNum == kStringNothing ? @"0":stringNum]];
    
    if ([[result componentsSeparatedByString:@"."] count] > 1){
        return result;
    }else{
        return [result stringByAppendingString:@"."];
    }
}


- (NSString *)precisionFormatStringNum:(NSString *)stringNum
{
    NSNumberFormatter *formatter = [[[NSNumberFormatter alloc] init] autorelease];
    
    [formatter setMinimumIntegerDigits:1];
    [formatter setRoundingMode:NSNumberFormatterRoundHalfUp];
    [formatter setMinimumFractionDigits:[SDKRegistry returnIntFromComponent:@"Precision"]];
    [formatter setMaximumFractionDigits:[SDKRegistry returnIntFromComponent:@"Precision"]];
    [formatter setNumberStyle:[SDKRegistry returnBoolFromComponent:@"GroupDigits"] ? NSNumberFormatterDecimalStyle:NSNumberFormatterNoStyle];
    
    NSString *result = [formatter stringFromNumber:[formatter numberFromString:stringNum == kStringNothing ? @"0":stringNum]];
    
    if ([[result componentsSeparatedByString:@"."] count] > 1){
        return result;
    }else{
        return [result stringByAppendingString:@"."];
    }
}

- (BOOL)populateContent
{
    SDKDatabase *database = [[[SDKDatabase alloc] init] autorelease];
    
    [self setSuccess:NO];
    
    if ([database databaseExistsCheck]){
        [self.content removeAllObjects];
        
        Sqlite *sqlitedb = [[Sqlite alloc] init];
        
        if (![sqlitedb open:[SDKRegistry returnNSStringFromComponent:@"sqliteStore"]]) return NO;
        
        NSArray *results = [sqlitedb executeQuery:@"SELECT * FROM Currencies ORDER BY mm_name;"];
        
        if ([results count] > 0){
            [self setSuccess:YES];
            
            for (NSDictionary *dictionary in results){
                for (NSString *field in [dictionary keyEnumerator]){
                    if ([field isEqualToString:@"mm_code"]){
                        [self.content addObject:[dictionary objectForKey:field]];
                    }
                }
            }
        }
    }
    
    return self.success;
}

#pragma mark - SQLite Database Methods

- (BOOL)selectCurrencyWithCode:(NSString *)mm_code
{
    if (!mm_code || mm_code == @"") return NO;
    
    Sqlite *sqlitedb = [[Sqlite alloc] init];
    
    [self setSuccess:NO];
    
    if (![sqlitedb open:[SDKRegistry returnNSStringFromComponent:@"sqliteStore"]]) return NO;
    
    NSString *sqlQuery = [NSString stringWithFormat:@"SELECT * FROM Currencies WHERE mm_code = ?;"];
    
    NSArray *results = [sqlitedb executeQuery:sqlQuery, [NSString stringWithString:mm_code]];
    
    if ([results count] > 0){
        [self setSuccess:YES];
        
        for (NSDictionary *dictionary in results){
            for (NSString *field in [dictionary keyEnumerator]){
                if ([field isEqualToString:@"mm_code"]){
                    self.code = [dictionary objectForKey:field];
                }else if ([field isEqualToString:@"mm_name"]){
                    self.name = [dictionary objectForKey:field];
                }else if ([field isEqualToString:@"mm_symbol"]){
                    self.symbol = ([self getSymbolForCode:[dictionary objectForKey:field]]);
                }else if ([field isEqualToString:@"mm_rate"]){
                    self.rate = [[dictionary objectForKey:field] floatValue];
                }
            }
        }
    }
    
    [sqlitedb release];
    [results release];
    
    return self.success;
}

- (NSString *)getSymbolForCode:(NSString *)cc
{
    if ([cc isEqualToString:@"Eu."]) return @"€";
    if ([cc isEqualToString:@"Pd."]) return @"£";
    if ([cc isEqualToString:@"Yn."]) return @"¥";
    
    return cc;
}
